            const posts = [];
            const postRegex = /\[帖子\]([\s\S]*?)\[\/帖子\]/g;
            let match;
            while ((match = postRegex.exec(text)) !== null) {
                const content = match[1].trim();
                const postData = {};
                const imageDescMatch = content.match(/图片描述: ([\s\S]*?)\n标题:/);
                const titleMatch = content.match(/标题: ([\s\S]*?)\n正文:/);
                const textMatch = content.match(/正文:\n([\s\S]*?)\n发布者:/);
                const authorMatch = content.match(/发布者: ([\s\S]*?)\n评论:/);
                const commentsMatch = content.match(/评论:\n([\s\S]*?)\n标签:/);
                const tagsMatch = content.match(/标签: ([\s\S]*)/);

                if (imageDescMatch) postData.image_desc = imageDescMatch[1].trim();
                if (titleMatch) postData.title = titleMatch[1].trim();
                if (textMatch) postData.text = textMatch[1].trim();
                if (authorMatch) postData.author = authorMatch[1].trim();
                if (commentsMatch) postData.comments = commentsMatch[1].trim().split('\n').map(c => c.replace(/[\(\)-]/g, '').trim()).filter(c => c);
                if (tagsMatch) postData.tags = tagsMatch[1].trim().split(/\s+/);

                posts.push(postData);
            }
            return posts;
        }

        function renderPosts(posts) {
            postsContainer.innerHTML = '';
            if (!posts || posts.length === 0) return;
            posts.forEach(post => {
                const card = document.createElement('article');
                card.className = 'post-card';

                let commentsHTML = '';
                if (post.comments && post.comments.length > 0) {
                    commentsHTML = '<div class="comments-section">' + post.comments.map(c => `<div class="comment">${c}</div>`).join('') + '</div>';
                }

                let tagsHTML = '';
                if(post.tags && post.tags.length > 0) {
                    tagsHTML = '<div class="tags-container">' + post.tags.map(t => `<span class="tag">${t}</span>`).join('') + '</div>';
