            e.preventDefault(); const searchTerm = searchInput.value.trim();
            if (searchTerm) {
                const message = `/send "搜索：${searchTerm.replace(/"/g, '\\"')}" | /trigger await=true`;
                triggerSlash(message);
                searchInput.value = ''; searchModalOverlay.classList.remove('visible');
            }
        });

        postsContainer.addEventListener('click', (e) => {
            const header = e.target.closest('.post-header');
            if (header) {
                const card = header.closest('.post-card');
                if (card) {
                    card.classList.toggle('expanded');
                }
            }
        });
    });
    </script>
